(function($) {
	
	$(window).resize(function() {
		carregarRecursoMenu();
	}); 
	
	$(document).ready(function() {			    		    	
		carregarRecursoMenu();    	
    });

	
	responsive = function(){
		if($(window).width() < 500){
			$('#page-top').addClass("sidebar-toggled");
			$('#accordionSidebar').addClass("toggled");					
		}
		$(document).height() == $(window).height()  ? $(".scroll-to-down").fadeOut()	 : $(".scroll-to-down").fadeIn();
	};
	
	carregarRecursoMenu =  function(){ 
		responsive();
		
		$("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel",
				function(o) {
					if (768 < t(window).width()) {
						var e = o.originalEvent, l = e.wheelDelta || -e.detail;
						this.scrollTop += 30 * (l < 0 ? 1 : -1), o.preventDefault()
					}
				});
		
		$("#sidebarToggle, #sidebarToggleTop").on("click",
				function() {						
					$("#menuIncluido").addClass("expandMenuIncluido"),
					$("body").toggleClass("sidebar-toggled"), 
					$(".sidebar").toggleClass("toggled"), 
					$(".sidebar").hasClass("toggled") && $(".sidebar .collapse").collapse("hide")  && $("#menuIncluido").removeClass("expandMenuIncluido");
				});
		
		$(document).on("scroll",
				function() {
			
					100 < $(this).scrollTop() ? $(".scroll-to-top").fadeIn()	 : $(".scroll-to-top").fadeOut();
					
					$(this).height() == $(this).scrollTop() ? $(".scroll-to-down").fadeOut()	 : $(".scroll-to-down").fadeIn();
					
//						$('#page-top').addClass("sidebar-toggled");
//						$('#accordionSidebar').addClass("toggled");
					
		});

		$(document).on("click", "a.scroll-to-top", function(o) {
			var e = $(this);
				$("html, body").stop().animate({
						scrollTop : $(e.attr("href")).offset().top
				}, 1e3, "easeInOutExpo"), o.preventDefault()
		});
		
		$(document).on("click", "a.scroll-to-down", function(o) {
				$("html, body").stop().animate({						
					scrollTop : $(document).height()
				}, 1e3, "easeInOutExpo"), o.preventDefault()
		});
		
	};
	
	
	
	
}(jQuery));